class Score extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      score: 0
    };

    this.incrementScore = this.incrementScore.bind(this);
  }
  incrementScore() {
    this.setState({
      score: this.state.score++
    });
  }
  render() {
    return React.createElement(
      "div",
      null,
      React.createElement(
        "h2",
        null,
        "Score board for the ",
        this.props.teamName
      ),
      React.createElement(
        "div",
        null,
        "Score: ",
        this.state.score,
        " ",
        React.createElement("br", null),
        React.createElement(
          "button",
          { onClick: this.incrementScore },
          "+"
        ),
        React.createElement(
          "button",
          null,
          "-"
        )
      )
    );
  }
}

let el = document.getElementById('two');
ReactDOM.render(React.createElement(Score, { teamName: "Warriors" }), el);
